<?phpnamespace app\admin\controller;use \app\admin\controller;/* * To change this license header, choose License Headers in Project Properties. * To change this template file, choose Tools | Templates * and open the template in the editor. *//** * Description of Index * * @author 27532 */class Index extends Base{    //put your code here    public function index(){        if ($this->isMobile == 1) {            $this->assign('currentMenu',array('menu'=>'menu1','nav'=>'nav0'));            $this->assign('title','管理后台');            return $this->fetch('index/mobile_index');        } else {            $this->assign('currentMenu',array('menu'=>'menu1','nav'=>'nav0'));            $this->assign('title','管理后台');            return $this->fetch();        }    }    public function orders() {        $a = array();        $res = array();        foreach ($a as $k => $v) {            $pp = \app\admin\model\Order::get(['OrdNum' => $v]);            if (!$pp) $res[] = $v;        }        print_r(implode($res, ','));    }}