).attr('name') == 'father'){                        $('.popup_menu').hide();                        alert('只能删除子仓库！');                        return;                    }                                        $("#"+obj.target.id).remove();                    $('.popup_menu').hide();                });                return popupMenuApp;            }        }})();    //取消右键    $('.product').on('contextmenu', function (){return false;}).click(function(){        $('.popup_menu').hide();    });    //桌面点击右击    $('.product').on('contextmenu',function (e){        var popupmenu = kyoPopupMenu.sys(e);        l = ($(document).width() - e.clientX) < popupmenu.width() ? (e.clientX - popupmenu.width()) : e.clientX;        t = ($(document).height() - e.clientY) < popupmenu.height() ? (e.clientY - popupmenu.height()) : e.clientY;        popupmenu.css({left: l,top: t}).show();        return false;    });})</script><div class="canvas_title do-clear">    <ul class="tab_nav fl">        <li class="current"><a href="javaScript:void(0)">仓库新建</a></li>    </ul>    </div><div class="canvas_intro">    <ul class="add_from">        <input type="hidden" class="epaddbtn" value="1">        <input type="hidden" class="epdeletebtn" value="1">        <li>           <label class="label">库位名：<i>*</i></label>           <br/><input name="name" type="text" value="<?php echo $warehouse_name; ?>" id="name" />        </li>        <li>           <label class="label">几层：<i>*</i></label>           <input name="p_x" type="text" value="" id="p_x" />        </li>                 <li>            <label class="label">几列：<i>*</i></label>            <input name="p_y" type="text" value="" id="p_y" />        </li>          <li>           <label class="label">状态:</label>           <input type="radio" name="status" id="display" checked="checked"  value="1" />               <label for="display" >上架</label>           <input type="radio" name="status"