<!doctype html><html><head><meta charset="utf-8"><meta http-equiv="X-UA-Compatible" content="IE=edge,chrome=1"><meta name="viewport" content="width=device-width, initial-scale=1.0"><meta content="width=device-width, initial-scale=1.0, maximum-scale=1.0,  user-scalable=0" name="viewport" /><title>后台登录</title><link rel="stylesheet" href="<?php echo ADMIN_STYLE_URL; ?>css/r.css" type="text/css" media="screen" /><link rel="stylesheet" href="<?php echo ADMIN_STYLE_URL; ?>css/style.css?v=1.0" type="text/css" media="screen" /><script type="text/javascript" src="<?php echo ADMIN_STYLE_URL; ?>js/jquery.min.js"></script><script type="text/javascript" src="<?php echo LAYER_JS_URL; ?>layer.js"></script><?php if(isset($eventJS)): ?><script type="text/javascript" src="<?php echo ADMIN_STYLE_URL; ?>js/<?php echo $eventJS; ?>.js"></script><?php endif; ?><script>    $(function(){        code.imgurl="<?php echo url('/admin/login/code'); ?>";        code.Img=$("#codeimg");        login.UserName=$('#user');        login.PassWord=$('#pwd');        login.Code=$('#code');        login.Tip=$('#tiptxt');        login.PostURL="<?php echo url('/admin/login/checklogin'); ?>";        login.IndexURL="<?php echo url('/admin/index'); ?>";        login.entEvent();        var isMobile = "<?php echo $isMobile; ?>";        if (isMobile == 1) {            $("#account_login").hide();            $("#usercode_login").show();            $("#account").hide();            document.getElementById('user_code').focus();        } else {            document.getElementById('user').focus();        }    })</script><style>    .tab_nav_login {        top: 5px;        height: 40px;        overflow: hidden;    }    .login_nav {        top: 5px;        height: 20px;        overflow: hidden;    }    .tab_nav_login li{float:left; height:35px; margin-right:6px; -moz-border-radius: 4px; -webkit-border-radius: 4px; border-radius:4px;}    .tab_nav_login li a{display:block; padding:0 25px; line-height:33px;}    .tab_nav_login li.current,    .tab_nav_login li:hover{background:#2f96b4;}    .tab_nav_login li.enabled:hover{background:#eeeeee; }    .tab_nav_login li.enabled a{cursor:default;}    .current {        background-color: #828282;    }</style></head><body class="login">   <div class="loginbox">      <h1><a href="#"><img src="<?php echo ADMIN_STYLE_URL; ?>images/login_logo.png" /></a></h1>      <div class="login-form">          <div style="display:inline;" class="nacvs"><ul class="login_nav"></ul></div>        <div style="display:inline;">            <ul class="tab_nav_login">                <li style="" class="current" id="account"><h2><a href="javaScript:user_login.btn_show('account_login')">会员登录</a></h2></li>            <li style="" class="current"><h2><a href="javaScript:user_login.btn_show('usercode_login')">扫码登入</a></h2></li>            </ul>        </div>        <div id="account_login">         <input name="" id="user" type="text" placeholder="账号">         <input name="" id="pwd" type="password" placeholder="密码">         <div class="yzm do-clear">            <input name="" id="code" type="text" class="code" placeholder="验证码">            <img title="点击刷新" id="codeimg" src="<?php echo url('/admin/login/code'); ?>" onclick="code.img()" />         </div>         <a href="javaScript:login.post()" class="btn">登录</a>         <span id="tiptxt" class="logintip"></span>        </div>      <div id="usercode_login" style="display: none;">          <input name="" id="user_code" type="password" placeholder="登录码">          <a href="javaScript:user_login.user_post()" class="btn">登录</a>      </div>      </div>   </div></body><script>    var user_login = {        //登入方式切换        btn_show:function (btn) {            if (btn == 'account_login') {                $("#" + btn).show();                $("#usercode_login").hide();                document.getElementById('user').focus();            } else {                $("#" + btn).show();                $("#account_login").hide();                document.getElementById('user_code').focus();            }        },        //登录码提交        user_post:function() {            var user_code = $("#user_code").val();            if (user_code == '') {                alert('登录码不能为空');                return;            }            $.post("<?php echo url('/admin/login/usercode_checklogin'); ?>",{user_code:user_code},function(data){                if(data.code == 1000){                    window.location.href = "<?php echo url('/admin/index'); ?>";                }else{                    code.img();                    alert(data.msg);                }            });        }    };</script></html>